import React, { useRef, useState } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each, tree, min, descedants } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';
import Clean from './clean.js';



const Plot = ({recipe, width, height, color, dim}) => {

  var root =
  (<Clean
    recipe={recipe}
    dim={dim}

  />)



  var allVessels = root.descendants().filter(d => ('vessel' in d.data)).filter(d => (d.depth !== 0))

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <g>
        {ingredientNodes}
        {actionNodes}
        {vessel}
      </g>
    )
  })


  return (
      <g transform={`translate(${0}, ${50})`}>
        {flows}
      </g>
  );
};

export default Plot;
