import React, { useRef, useState, useEffect } from 'react';
import { select, ascending, transition, easeCubicIn, easeCubicOut } from 'd3';

import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';
import color from '../../media/theme/colors.json';
import dim from '../../media/theme/dim.json';

const Plot = ({recipe}) => {

  const [focus, setFocus] = useState(false);
  const prevFocus = useRef(false);


  useEffect(() => {

    if(!(focus == false)){
        var cur = select(focus[0].parentElement)
        var all = select(focus[0].parentElement.parentElement).selectAll('g')

        var t1 = transition()
          .duration(180)
          .ease(easeCubicOut)
        var t2 = transition()
          .duration(1000)
          .ease(easeCubicIn)


        all
          // .attr('filter', 'none')
          // .attr('opacity', 1)
          // .transition(t1)
          .attr('filter', 'url(#blur)')
          .attr('opacity', .3)

        cur
          .attr('filter', 'none')
          .attr('opacity', 1)


        var curX = cur.select('circle').attr('cx')
        var curY = cur.select('circle').attr('cy')

        var rect = cur.append('rect')
        .attr('x', +curX + 10)

          .attr('x', +curX + 10)
          .attr('y', +curY - dim.i.font.size - 1)
          .attr('width', 160)
          .attr('height', dim.i.font.size * 2)
          .attr('fill', color.background)
          .attr('opacity', 1)

        var amt = cur.append('text')
          .attr('x', +curX + 10)
          .attr('y', +curY)
          .attr('fill', color.blue2)
          .attr('font-size', dim.i.font.detailSize)
          .attr('alignment-baseline', 'middle')
          .text( focus[1].data.detail )
        amt.raise()

        if (!(prevFocus == false)){
          var prev = select(prevFocus.current[0].parentElement)

          prev.remove('.focus')

          console.log(focus)
          console.log(prevFocus.current)
          console.log('------')



          //
          // all
          //   .attr('filter', 'url(#blur)')
          //   .attr('opacity', .3)
          //
          // prev
          //   .attr('filter', 'none')
          //   .attr('opacity', 1)



        }
        prevFocus.current = focus

      }
    }, [focus])



  var root = recipe

  var allVessels = root.descendants().filter(d => ('vessel' in d.data)).filter(d => (d.depth !== 0)).sort((a,b) => {return ascending(a.data.stepOrder, b.data.stepOrder)} )

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
            focus={focus}
            setFocus={setFocus}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <>
        {vessel}
        {actionNodes}
        {ingredientNodes}
      </>
    )
  })

  return (
      <g transform={`translate(${0}, ${50})`}>
        <defs>
          <filter id="blur" x="-0.08" y="0">
            <feGaussianBlur in="SourceGraphic" stdDeviation="1" />
            <feOffset dx="0" dy="0" />
          </filter>
          <filter id="shadow" x="0" y="0" width="200%" height="200%">
            <feOffset result="offOut" in="SourceAlpha" dx=".1" dy=".1" />
            <feGaussianBlur result="blurOut" in="offOut" stdDeviation=".1" />
            <feBlend in="SourceGraphic" in2="blurOut" mode="normal" />
          </filter>
        </defs>
        {flows}
      </g>
  );
};

export default Plot;
