import React, { useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each, tree } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';


const Plot = ({recipe, width, height, color, dim}) => {

  // const canvasWidth = width - 2*dim.m.padding;
  // const canvasHeight = height - 2*dim.m.padding;

  var root = hierarchy(recipe.recipe[0]);

  const testTree = tree().size([300, 600])(root)

  const desc = (a) => a.descendants()
  root.each((d, i) => { d.order = i })

  var iorderCount = 0
  root.eachAfter(
    function(d){
      if ('vessel' in d.data) {
        iorderCount = 0
      } else {
        d.data.iorder = iorderCount
        iorderCount += 1
      }
    }
  )

  root.count()
  root.each(d => { d.vesselSibs = (d.parent ? d.parent.children.filter(a => ("vessel" in a.data)) : 'none' )})

  root.each(
    function(d, i){
      if (d.depth == 0){
        d.pHeight = 40
      }
      else if ('vessel' in d.data){
        d.numTasks = d.children.filter(a => !("vessel" in a.data)).length
        d.numActions = d.children.filter(a => !("vessel" in a.data)).length
        d.numIng = d.children.filter(a => ("ingredient" in a.data)).length


        d.numShove = d.parent.children.filter(a => ("vessel" in a.data)).filter(a => (d.data.vorder > a.data.vorder)).length
      }
    }
  )

  var allVessels = root.descendants().filter(d =>'vessel' in d.data)

  root.each(function(d){
    if (d.depth == 0){
      d.posY = 1000
      d.posX = 10
    }
    else if ('vessel' in d.data) {
      d.posX = d.parent.posX + (d.numShove * dim.v.shove)
      var posYAdd = []
      allVessels.forEach(a => (d.data.vorder > a.data.vorder) ? posYAdd.push(dim.v.marginBottom + ((a.numTasks+1) * dim.m.taskH) ) : null)
      d.posY = sum(posYAdd)
    } else {
      d.posY = d.parent.posY + ((d.data.iorder + 1) * dim.m.taskH)
      d.posX = d.parent.posX
    }
  })





  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  // descends = descends.filter(d => (d.depth !== 0))

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <>
        {/* {ingredientNodes} */}
        {/* {actionNodes} */}
        {vessel}
      </>
    )
  })


  return (
      <g
        transform={`translate(${0}, ${50})`}
        >
        {flows}
      </g>
  );
};

export default Plot;
