import React, { useState, useEffect, useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';
import '../../../css/plot.css'


const IngredientNode = ({ root, color, dim, a, b, j }) => {

  const [focus, setFocus] = useState(0);

  const statesIng = [
    {
      "textposX": b.data.pX + 30,
      "dotposX": b.data.pX + 20,
      "dotColor": color.blue2,
      "posXTransform": 'translate(50px, 0px)',
    },
    {
      "textposX": b.data.pX + 12,
      "dotposX": b.data.pX,
      "dotColor": color.blue1
    }
  ]



  const label = (
    <text
      className={'plot-text'}
      key={'ingredient' + b + j}
      // x={ statesIng[focus].textposX }
      x={ b.data.pX + 12 }
      y={ b.data.pY + 1}
      // style={{textTransform:'capitalize'}}

      style={{textTransform:'capitalize'}}

      fill={`${color.blue2}`}
      textAnchor={'start'}
      fontSize={dim.i.font.size}
      fontWeight={dim.i.font.weight}
      alignmentBaseline={'middle'}
      >
      {/* { b.data.stepOrder} */}

      { b.data.ingredient}

    </text>

  )
  const dot = (
    <circle
      key={'ingredient' + b + j}
      r={4}
      cx={ statesIng[focus].dotposX }
      cy={ b.data.pY }
      fill={`${statesIng[focus].dotColor}`}
      >
    </circle>

  )

  return (
    <g
      // ref={ focusRef }
      className={'plot-animation'}
      onClick={ () => setFocus(1-focus) }
      >
      {label}
      {dot}
    </g>

  );
};

export default IngredientNode;
