import React, { useState, useEffect, useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';
import { useSwipeable } from 'react-swipeable';
import '../../../css/plot.css'


const IngredientNode = ({ root, color, dim, a, b, j }) => {

  const handlers = useSwipeable({
    onSwipedLeft: () => slide(NEXT),
  });

  const [focus, setFocus] = useState(0);

  const statesIng = [
    {
      "textposX": b.data.pX + 30,
      "dotposX": b.data.pX + 20,
      "dotColor": color.blue2,
      "posXTransform": 'translate(18px, 0px)'
    },
    {
      "textposX": b.data.pX + 10,
      "dotposX": b.data.pX,
      "dotColor": color.blue1,
      "posXTransform": 'translate(0px, 0px)'
    }
  ]



  const label = (
    <text
      className={'plot-text'}
      className={'plot-animation'}
      key={'ingredient' + b + j}
      x={ b.data.pX + 10 }
      y={ b.data.pY + 1 }
      style={{textTransform:'capitalize'}}
      fill={`${statesIng[focus].dotColor}`}
      textAnchor={'start'}
      fontSize={dim.i.font.size}
      fontWeight={dim.i.font.weight}
      alignmentBaseline={'middle'}
      >
      {/* { b.data.stepOrder} */}

      { b.data.ingredient}

    </text>

  )
  const dot = (
    <circle
      className={'plot-animation'}

      key={'ingredient' + b + j}
      r={4}
      cx={ b.data.pX }
      cy={ b.data.pY }
      fill={`${statesIng[focus].dotColor}`}
      >
    </circle>

  )

  return (
    <g
      // ref={ focusRef }
      className={'plot-animation'}
      onClick={ () => setFocus(1-focus) }
      style={{transform: `${ statesIng[focus].posXTransform }`}}
      >
      {label}
      {dot}
    </g>

  );
};

export default IngredientNode;
