import React, { useRef, useState, useEffect } from 'react';
import { select, ascending } from 'd3';

import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';
import color from '../../media/theme/colors.json';
import dim from '../../media/theme/dim.json';

const Plot = ({recipe}) => {

  const [focus, setFocus] = useState(false);

  useEffect(() => {

    if(!(focus == false)){
        var cur = select(focus[0].parentElement)
        var others = select(focus[0].parentElement.parentElement).selectAll('g')

        others
          .attr('filter', 'url(#blur)')
          .attr('opacity', .3)
        cur
          .attr('filter', 'none')
          .attr('opacity', 1)


        var curX = cur.select('circle').attr('cx')
        var curY = cur.select('circle').attr('cy')

        console.log(cur)

        var rect = cur.append('rect')
          .attr('x', +curX + 10)
          .attr('y', +curY - dim.i.font.size - 1)
          .attr('width', 160)
          .attr('height', dim.i.font.size * 2)
          .attr('fill', 'white')
          .attr('opacity', 0.8)

        var amt = cur.append('text')
          .attr('x', +curX + 10)
          .attr('y', +curY)
          .attr('fill', color.blu2)
          .attr('alignment-baseline', 'middle')

          .text( focus[1].data.detail )




      }
    }, [focus])



  var root = recipe

  var allVessels = root.descendants().filter(d => ('vessel' in d.data)).filter(d => (d.depth !== 0)).sort((a,b) => {return ascending(a.data.stepOrder, b.data.stepOrder)} )

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
            focus={focus}
            setFocus={setFocus}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <>
        {vessel}
        {actionNodes}
        {ingredientNodes}
      </>
    )
  })

  return (
      <g transform={`translate(${0}, ${50})`}>
        <defs>
          <filter id="blur" x="-0.08" y="0">
            <feGaussianBlur in="SourceGraphic" stdDeviation="1" />
            <feOffset dx="0" dy="0" />
          </filter>
          <filter id="shadow" x="0" y="0" width="200%" height="200%">
            <feOffset result="offOut" in="SourceAlpha" dx=".1" dy=".1" />
            <feGaussianBlur result="blurOut" in="offOut" stdDeviation=".1" />
            <feBlend in="SourceGraphic" in2="blurOut" mode="normal" />
          </filter>
        </defs>
        {flows}
      </g>
  );
};

export default Plot;
