import React, { useRef } from 'react';

import { select, linkVertical } from 'd3';

const Vessel = ({ a, i, dim, color }) => {

  const shape = (
    <rect
      className={'vesselShape'}
      key={'c' + i}
      width={ dim.v.shape.width }
      height={ a.posHeight }
      x={ a.posX }
      y={ a.posY }
      transform={`translate(${dim.vessel.shape.width*-.5}, ${0})`}
      fill={ `${color.background}` }
      stroke={  `${color.green1}` }
      strokeWidth={ dim.vessel.shape.stroke }
      rx={ dim.vessel.shape.width * .5 }
      >
    </rect>
  )

  const label = (
    <text
      key={'c' + i}
      x={ a.posX }
      y={ a.posY }
      // transform={`translate(${dim.vessel.shape.width*-.5}, ${0})`}
      fill={ `${color.green2}` }
      fontSize={dim.v.textSize}
      >
        {a.data.vessel}
    </text>
  )


  return (
      <g>
        {shape}
        {label}
      </g>
  );
};

export default Vessel;
