import React, { useState, useEffect, useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';
import { useSwipeable } from 'react-swipeable';

import '../../../css/plot.css'


const Action = ({ root, color, dim, a, b, j, focusG, setFocusG }) => {

  const [focus, setFocus] = useState(0);

  const statesIng = [
    {
      "textposX": b.data.pX + 30,
      "dotposX": b.data.pX - dim.a.width/2 + 20,
      "dotColor": color.orange2,
      "posXTransform": 'translate(18px, 0px)'
    },
    {
      "textposX": b.data.pX + 10,
      "dotposX":  b.data.pX - dim.a.width/2,
      "dotColor": color.orange1,
      "posXTransform": 'translate(0px, 0px)'
    }
  ]

  const handlers = useSwipeable({
    onSwipedLeft: () => setFocus(1),
    onSwipedRight: () => setFocus(0),
    preventDefaultTouchmoveEvent: true

  });

  const label = (
    <text
      className={'plot-text'}
      className={'plot-animation'}
      key={'ingredient' + b + j}
      x={ b.data.pX + 10 }
      y={ b.data.pY }
      style={{textTransform:'capitalize'}}
      fill={`${statesIng[focus].dotColor}`}
      textAnchor={'start'}
      fontSize={dim.a.font.size}
      fontWeight={dim.a.font.weight}
      alignmentBaseline={'hanging'}
      >
      {/* { b.data.stepOrder} */}
      { b.data.action}

    </text>
  )

  const aHeight = b.data.action_amt * dim.a.amtScale

  const shape = (
    <rect
      className={'plot-animation'}
      key={'ingredient' + b + j}
      x={ b.data.pX - dim.a.width/2 }
      y={ b.data.pY - dim.a.width/2 }
      width={ dim.a.width }
      height={ aHeight + dim.a.width }
      fill={`${statesIng[focus].dotColor}`}
      rx={dim.a.width/2}
      >
    </rect>
  )

  // const dot = (
  //   <circle
  //     className={'name'}
  //     key={'ingredient' + b + j}
  //     r={4}
  //     cx={ b.data.pX + 20}
  //     cy={ b.data.pY }
  //     fill={`${color.orange3}`}
  //     >
  //   </circle>
  //
  // )

  return (
    <g
      {...handlers}

      className={'plot-animation'}
      onClick={ () => setFocus(1-focus) }
      style={{transform: `${ statesIng[focus].posXTransform }`}}
      >
      {shape}
      {label}
      {/* {dot} */}

    </g>

  );
};

export default Action;
