import React, { useRef, useState, useEffect } from 'react';
import dim from '../../media/theme/dim.json';
import color from '../../media/theme/colors.json';

const IngredientList = ({ ingredientList }) => {

const [windowWidth, setWindowWidth] = useState(window.innerWidth)

const listDisplay = [];

ingredientList.forEach((d,i) => {
  listDisplay.push(
    <text
      x={ 50 }
      y={ 50 +i * 20 }
      fill={color.blue2}
      textAnchor={'start'}
      fontSize={dim.i.font.size}
      alignmentBaseline={'middle'}
      >
      { d.data.ingredient }
      <tspan
        alignmentBaseline={'middle'}
        fontSize={dim.i.font.amountSize}
        fill={color.blue2}
        >
        {' - ' + d.data.ing_amt + ' ' + d.data.ing_unit}
      </tspan>
    </text>
  )
})

const dot = [];

ingredientList.forEach((d,i) => {
  dot.push(
    <circle
      cx={ 40 }
      cy={ 50 + i * 20 }
      r={4}
      fill={color.blue2}
      >
    </circle>
  )
})




  return (
      <svg width={windowWidth} height={400}>
        { dot }
        { listDisplay }
      </svg>
  );
};

export default IngredientList;
