import React, { useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessels from './vessels/vessels.js';



const Plot = ({recipe, width, height, color, dim}) => {

  const canvasWidth = width - 2*dim.main.padding;
  const canvasHeight = height - 2*dim.main.padding;
  //
  // var root2 = hierarchy(recipe);
  // console.log(root2)


  var root = hierarchy(recipe.recipe[0]);


  const desc = (a) => a.descendants()

  root = root.each(d => { d.vesselSibs = (d.parent ? d.parent.children.filter(a => ("vessel" in a.data)) : 'none' )})


  root.each(
    function(d, i){
      if (d.depth == 0){
        console.log(i)
        d.pHeight = 40
      }
      else if ('vessel' in d.data){
        console.log(i)
        d.pHeight = (d.children.filter(a => !("vessel" in a.data) ).length) * 20
      }
    }
  )

  console.log(root)


  root.each(function(d){
    if (d.depth == 0){
      d.posY = 1000
      d.posX = 100

    }
    else if ('vessel' in d.data) {
      var posYAdd = []
      var descend = desc(d)
      descend.shift()
      descend.forEach(a => ('vessel' in a.data) ? posYAdd.push(a.posHeight) : null)
      d.vesselSibs.forEach(b => (b.order < d.order) ? posYAdd.push(b.posHeight) : null)
      posYAdd.push((posYAdd.length)*(dim.vessel.shape.marginHeight))
      d.posY = sum(posYAdd)
      d.posX = (dim.vessel.shape.width*.5) + ((posYAdd.length-1) * (dim.vessel.shape.width + (dim.vessel.shape.marginWidth*2)))
    }
  })


  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  var allVessels = root.descendants().filter(d =>'vessel' in d.data)
  // descends = descends.filter(d => (d.depth !== 0))

  var flows = []

  allVessels.forEach((a,i) => {
    const vessel = []
    vessel.push(
      <Vessels
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    flows.push(
      <>
        {vessel}
      </>
    )
  })


  return (
      <g>
        {flows}
      </g>
  );
};

export default Plot;
