import React, { useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each, tree, min } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';


const Plot = ({recipe, width, height, color, dim}) => {
  var root = hierarchy(recipe.recipe[0]);

  root.each(
    function(d, i){
      if (d.depth == 0){
      }
      else if ('vessel' in d.data){
        d.actions = d.children.filter(a => ("action" in a.data)).map(b => b.data.action_amt)
        d.numIng = d.children.filter(a => ("ingredient" in a.data)).length
        d.numShove = d.parent.children.filter(a => ("vessel" in a.data)).filter(a => (d.data.vorder > a.data.vorder)).length
      }
    }
  )

  var pY = 0
  var pH = 0
  var pYMax = 0
  root.each(
    function(d){
      if ('vessel' in d.data) {
        if (d.depth == 0) {
          pH = dim.m.serveHeight
          pY += pH
          d.data.pH = pH
          d.data.pY = pY
        } else {
          pH = (sum(d.actions) * dim.a.amtScale) + (d.numIng * dim.i.height)
          pY = d.parent.data.pY
          pY += pH
          d.data.pY = pY
          d.data.pH = pH
          if ((pY) > pYMax){
            pYMax = pY
          }
        }
      }
    }
  )

  pY = 0
  pH = 0
  var taskCount = 0
  root.eachBefore(
    function(d){
      if ('vessel' in d.data) {
        taskCount = 0
        if (d.depth == 0) {
          pY = pYMax
          pH = dim.m.serveHeight
          pY -= pH
          d.data.pH = pH
          d.data.pY = pYMax
        } else {
          pH = 0
          pH += (sum(d.actions) * dim.a.amtScale) + (d.numIng * dim.i.height)
          pY = d.parent.data.pY
          pY -= pH
          d.data.pY = pY
          d.data.pH = pH
        }
      } else {
        if ('action' in d.data) {
          pY = d.parent.data.pY
          taskCount += d.data.action_amt * dim.a.amtScale
          console.log(d.parent.data.vessel, ' ', d.data.action, taskCount)
          d.data.pY = pY + taskCount
          d.data.pH = pY + taskCount

        } else if ('ingredient' in d.data) {
          pY = d.parent.data.pY
          taskCount += dim.i.height
          d.data.pY = pY + taskCount
        }
      }
    }
  )

  const getLeafY = (a) => {
    var leaves = a.leaves()
    leaves = leaves.map(b => b.data.pY)
    return min(leaves)
  }

  var pX = 20
  root.each(
    function(d){
      if ('vessel' in d.data) {
        if (d.depth == 0) {
          d.data.pX = pX
        } else {
          pX = d.parent.data.pX
          var vesselSibs = d.parent.children.filter(a => ("vessel" in a.data))
          var numLess = vesselSibs.filter(b => (getLeafY(b) < getLeafY(d)) ).length
          pX += numLess * 40
          d.data.pX = pX
        }
      } else {
        if ('action' in d.data){
          pX = d.parent.data.pX + 10
          d.data.pX = pX
        } else if ('ingredient' in d.data){
          pX = d.parent.data.pX + 10
          d.data.pX = pX
        }
      }
    })

  var allVessels = root.descendants().filter(d => ('vessel' in d.data))


  console.log(allVessels)
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <g>
        {ingredientNodes}
        {actionNodes}
        {vessel}
      </g>
    )
  })


  return (
      <g
        transform={`translate(${0}, ${100})`}

        >
        {flows}
      </g>
  );
};

export default Plot;
