import React, { useRef } from 'react';

import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';


const IngredientNode = ({ root, color, dim, a, b, j, focusG, setFocusG }) => {

  const focusRef = useRef();


  const label = (
    <text
      className={'plot-text'}
      key={'ingredient' + b + j}
      x={ b.data.pX + 12}
      y={ b.data.pY + 1}
      style={{textTransform:'capitalize'}}
      fill={`${color.blue2}`}
      textAnchor={'start'}
      fontSize={dim.i.font.size}
      fontWeight={dim.i.font.weight}
      alignmentBaseline={'middle'}
      >
      { b.data.stepOrder}

      { b.data.ingredient}

    </text>

  )
  const dot = (
    <circle
      key={'ingredient' + b + j}
      r={4}
      cx={ b.data.pX }
      cy={ b.data.pY }
      fill={`${color.blue1}`}
      >
    </circle>

  )

  return (
    <g>
      {label}
      {dot}
    </g>

  );
};

export default IngredientNode;
