import '../../App.css';
import PlotMaster from './plot/plottMaster.js';
import ListIngredients from './lists/ingredients.js';
import plotRecipe from './plot/plotRecipe.js';


const PageMaster = ({ recipe }) => {


  var recipeAndHeight = plotRecipe(recipe)
  var recipePlot = recipeAndHeight[0]
  var svgHeight = recipeAndHeight[1] + 300

  const [windowWidth, setWindowWidth] = useState(window.innerWidth)
  const [windowHeight, setWindowHeight] = useState(window.innerHeight)


  return (
    <div>
        {/* <ListIngredients
          recipe={recipe}
        /> */}
        <PlotMaster
          recipePlot={recipePlot}
          svgHeight={svgHeight}
        />
    </div>
  );
};

export default PageMaster;
