import React, { useRef } from 'react';

import { select, linkVertical } from 'd3';

const Vessel = ({ a, i, dim, color }) => {

  const shape = (
    <rect
      className={'vesselShape'}
      key={'c' + i}
      width={ dim.v.s.width }
      height={ (a.numTasks+1) * dim.m.taskH }
      x={ a.posX }
      y={ a.posY }
      transform={`translate(${dim.v.s.width*-.5}, ${0})`}
      fill={ `${color.background}` }
      stroke={  `${color.green1}` }
      strokeWidth={ dim.v.s.stroke }
      rx={ dim.v.s.width * .5 }
      >
    </rect>
  )

  const label = (
    <text
      key={'c' + i}
      x={ a.posX }
      y={ a.posY }
      // transform={`translate(${dim.vessel.shape.width*-.5}, ${0})`}
      fill={ `${color.green2}` }
      fontSize={dim.v.textSize}
      // alignmentBaseline={'middle'}

      >
        {/* {a.data.vessel} */}
        {a.posY}

    </text>
  )
  const label2 = (
    <text
      key={'c' + i}
      x={ a.posX }
      y={ a.posY + a.data.diffMax }
      // transform={`translate(${dim.vessel.shape.width*-.5}, ${0})`}
      fill={ `${color.green2}` }
      fontSize={dim.v.textSize}
      // alignmentBaseline={'middle'}

      >
        {a.data.vessel}
        {a.posY}

    </text>
  )


  return (
      <g>
        {/* {shape} */}
        {label}
        {label2}

      </g>
  );
};

export default Vessel;
