import React, { useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each, tree, min } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessel from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';
import Action from './actions/actions.js';


const Plot = ({recipe, width, height, color, dim}) => {

  // const canvasWidth = width - 2*dim.m.padding;
  // const canvasHeight = height - 2*dim.m.padding;

  var root = hierarchy(recipe.recipe[0]);


  const desc = (a) => a.descendants()
  root.each((d, i) => { d.order = i })



  root.count()
  root.each(d => { d.vesselSibs = (d.parent ? d.parent.children.filter(a => ("vessel" in a.data)) : 'none' )})

  root.each(
    function(d, i){
      if (d.depth == 0){
        d.pHeight = 40
      }
      else if ('vessel' in d.data){
        d.actions = d.children.filter(a => ("action" in a.data)).map(b => b.data.action_amt)
        d.numIng = d.children.filter(a => ("ingredient" in a.data)).length
        d.numShove = d.parent.children.filter(a => ("vessel" in a.data)).filter(a => (d.data.vorder > a.data.vorder)).length


      }
    }
  )

  var pY = 0
  var pH = 0
  var pHpYMax = 0
  root.each(
    function(d){
      if ('vessel' in d.data) {
        if (d.depth == 0) {
          d.data.pY = pY
          pH += 20
          d.data.pH = pH
        } else {
          pH = 20
          pH += (sum(d.actions) * dim.a.amtScale) + (d.numIng * dim.i.height)
          pY = d.parent.data.pY
          pY += pH
          d.data.pY = pY
          d.data.pH = pH
          if ((pH + pY) > pHpYMax){
            pHpYMax = pH + pY
          }
        }
      }
    }
  )



  pY = 0
  pH = 0
  var taskCount = 0
  root.each(
    function(d){
      if ('vessel' in d.data) {
        taskCount = 0
        if (d.depth == 0) {
          d.data.pY = pHpYMax
          pH += 20
          d.data.pH = pH
        } else {
          pH = 20
          pH += (sum(d.actions) * dim.a.amtScale) + (d.numIng * dim.i.height)
          pY = d.parent.data.pY
          pY -= pH
          d.data.pY = pY
          d.data.pH = pH
        }
      } else {
        if ('action' in d.data){
          pY = d.parent.data.pY
          taskCount += d.data.action_amt * dim.a.amtScale
          d.data.pY = pY + taskCount
        } else if ('ingredient' in d.data){
          pY = d.parent.data.pY
          taskCount += dim.i.height
          d.data.pY = pY + taskCount
        }
      }
    }
  )

  const getLeafY = (a) => {
    var leaves = a.leaves()
    leaves.map(b => b.data.pY)
    return min(leaves)
  }
  var pX = 20

  root.each(
    function(d){
      if ('vessel' in d.data) {
        if (d.depth == 0) {
          d.data.pX = pX
        } else {

          pX = d.parent.data.pX

          var vesselSibs = d.parent.children.filter(a => ("vessel" in a.data))

          var numLess = vesselSibs.filter(b => (getLeafY(b) > getLeafY(d)) ).length

          console.log(numLess)
          //
          // console.log(numLess)
          pX += numLess * 40
          d.data.pX = pX
        }
      } else {
        if ('action' in d.data){
          pX = d.parent.data.pX + 10
          d.data.pX = pX
        } else if ('ingredient' in d.data){
          pX = d.parent.data.pX + 10
          d.data.pX = pX
        }
      }
    }
  )

  var allVessels = root.descendants().filter(d =>'vessel' in d.data)

  root.each(function(d){
    if (d.depth == 0){
      d.posY = 1000
      d.posX = 10
    }
    else if ('vessel' in d.data) {

      d.posX = d.parent.posX + (d.numShove * dim.v.shove)

      var posYAdd = []

      allVessels.forEach(
        function(a){
          if(d.data.vorder < a.data.vorder){
            posYAdd.push(dim.v.marginBottom + ((sum(a.actions) * dim.a.amtScale) + (a.numIng * dim.i.height)))
          }
        }
      )
      if (d.numShove !== 0) {
        posYAdd.push(-d.data.pY, -dim.v.textSize)
      }
      d.posY = sum(posYAdd)

    } else {
      d.posX = d.parent.posX + 10
      d.posY = d.parent.posY + d.data.diff
    }
  })





  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  // descends = descends.filter(d => (d.depth !== 0))

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessel
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      if ('ingredient' in b.data) {
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    const actionNodes = []

    a.children.forEach((b,j) => {
      if ('action' in b.data) {
        actionNodes.push(
          <Action
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />)
        }
      })

    flows.push(
      <g>
        {/* {ingredientNodes} */}
        {/* {actionNodes} */}
        {vessel}
      </g>
    )
  })


  return (
      <g
        transform={`translate(${0}, ${100})`}

        >
        {flows}
      </g>
  );
};

export default Plot;
