import React, { useRef } from 'react';

import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';


const Action = ({ root, color, dim, a, b, j, focusG, setFocusG }) => {


  const label = (
    <text
      className={'plot-text'}
      key={'ingredient' + b + j}
      x={ b.data.pX + 12}
      y={ b.data.pY }
      style={{textTransform:'capitalize'}}
      fill={`${color.orange2}`}
      textAnchor={'start'}
      fontSize={dim.a.font.size}
      fontWeight={dim.a.font.weight}
      alignmentBaseline={'hanging'}
      >
      {/* { b.data.stepOrder} */}
      { b.data.action}

    </text>
  )

  const aHeight = b.data.action_amt * dim.a.amtScale

  const shape = (
    <rect
      key={'ingredient' + b + j}
      x={ b.data.pX - dim.a.width/2 }
      y={ b.data.pY - dim.a.width/2 }
      width={ dim.a.width }
      height={ aHeight + dim.a.width }
      fill={`${color.orange1}`}
      rx={dim.a.width/2}
      >
    </rect>
  )

  const dot = (
    <circle
      className={'name'}
      key={'ingredient' + b + j}
      r={4}
      cx={ b.data.pX + 20}
      cy={ b.data.pY }
      fill={`${color.orange3}`}
      >
    </circle>

  )

  return (
    <g>
      {shape}
      {label}
      {/* {dot} */}

    </g>

  );
};

export default Action;
