  )

  console.log(root)

  root = root.each(function(d){
    if (d.depth == 0){
      d.posY = 500
    }
    else if ('vessel' in d.data) {
      var posYAdd = []
      var descend = desc(d)
      descend.shift()
      descend.forEach(a => ('vessel' in a.data) ? posYAdd.push(a.posHeight) : null)
      d.posY = sum(posYAdd)
      d.posX = 100
    }
  })



  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////


  var descends = root.descendants().filter(d =>'vessel' in d.data)
  descends = descends.filter(d => (d.depth !== 0))



  var flows = []

  descends.forEach((a,i) => {
    const vessel = []
    vessel.push(
      <Vessels
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    flows.push(
      <>
        {vessel}
      </>
    )
  })


  return (
      <g>
        {flows}
      </g>
  );
};

export default Plot;
