import React, { useRef } from 'react';
import { select, max, sum,  hierarchy, linkHorizontal, each, tree } from 'd3';
import { scaleLinear } from 'd3-scale';
import Vessels from './vessels/vessels.js';
import IngredientNode from './ingredients/ingredientNode.js';



const Plot = ({recipe, width, height, color, dim}) => {

  const canvasWidth = width - 2*dim.main.padding;
  const canvasHeight = height - 2*dim.main.padding;

  var root = hierarchy(recipe.recipe[0]);

  const testTree = tree().size([300, 600])(root)

  const desc = (a) => a.descendants()
  root.each((d, i) => { d.order = i })

  var iorderCount = 0
  root.eachAfter(
    function(d){
      if('vessel' in d.data){
        iorderCount = 0
      } else if('ingredient' in d.data){
        d.data.iorder = iorderCount
        iorderCount += 1
      }
    }
  )

  root.count()
  root.each(d => { d.vesselSibs = (d.parent ? d.parent.children.filter(a => ("vessel" in a.data)) : 'none' )})

  root.each(
    function(d, i){
      if (d.depth == 0){
        d.pHeight = 40
      }
      else if ('vessel' in d.data){
        // d.pHeight = (d.children.filter(a => !("vessel" in a.data)).length) * 20
        d.pHeight = d.children.length*10

      }
    }
  )


    const nodePosY = (a) => {
      return a.posY + ((a.data.iorder) * 5)
    }


  root.each(function(d){
    if (d.depth == 0){
      d.posY = 1000
      d.posX = 100
    }
    else if ('vessel' in d.data) {
      var posYAdd = []
      var descend = desc(d)
      descend.shift()
      descend.forEach(a => ('vessel' in a.data) ? posYAdd.push(a.pHeight) : null)
      d.vesselSibs.forEach(b => (d.data.vorder > b.data.vorder) ? posYAdd.push(b.value*30) : null)
      d.posY = sum(posYAdd)
      d.posX = 10
    } else if ('ingredient' in d.data) {
      d.posY = d.parent.posY + ((d.data.iorder) * 8)
      d.posX = d.parent.posX
    }
  })





  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  var allVessels = root.descendants().filter(d =>'vessel' in d.data)
  // descends = descends.filter(d => (d.depth !== 0))

  var flows = []

  allVessels.forEach((a,i) => {

    const vessel = []
    vessel.push(
      <Vessels
        a={a}
        i={i}
        dim={dim}
        color={color}
      />
    )

    const ingredientNodes = []

    a.children.forEach((b,j) => {
      ('ingredient' in b.data) ?
        ingredientNodes.push(
          <IngredientNode
            color={color}
            dim={dim}
            a={a}
            b={b}
            j={j}
          />
        ) : console.log('')
      }
    )



    flows.push(
      <>
        {ingredientNodes}
        {vessel}
      </>
    )
  })


  return (
      <g
        transform={`translate(${0}, ${50})`}
        >
        {flows}
      </g>
  );
};

export default Plot;
