import React, { useState, useEffect, useRef } from 'react';

import { select, max, sum,  hierarchy, linkHorizontal, easeSin, easeCubicIn, easeCubicOut, transition } from 'd3';


const IngredientNode = ({ root, color, dim, a, b, j }) => {

  const [focus, setFocus] = useState(0);

  const focusRef = useRef();

  const on = {
    "posX": b.data.pX + 12
  }

  const off = {
    "posX": b.data.pX + 30
  }


  var focusState = off

  useEffect(() => {
    console.log('hey')
    if(focus == 1){
      focusState=on
    } else if (focus == 0){
      focusState=off
    }

  }, [focus]);

  const label = (
    <text
      className={'plot-text'}
      key={'ingredient' + b + j}
      x={ b.data.pX + 12 + focus * 20 }
      y={ b.data.pY + 1}
      style={{textTransform:'capitalize'}}
      fill={`${color.blue2}`}
      textAnchor={'start'}
      fontSize={dim.i.font.size}
      fontWeight={dim.i.font.weight}
      alignmentBaseline={'middle'}
      >
      {/* { b.data.stepOrder} */}

      { b.data.ingredient}

    </text>

  )
  const dot = (
    <circle
      key={'ingredient' + b + j}
      r={4}
      cx={ b.data.pX }
      cy={ b.data.pY }
      fill={`${color.blue1}`}
      >
    </circle>

  )

  return (
    <g
      // ref={ focusRef }
      onClick={ () => setFocus(1-focus) }
      >
      {label}
      {dot}
    </g>

  );
};

export default IngredientNode;
